    this.select.options.forEach((item: MatOption) => { 
      if(!item.disabled) {
        item.select();
      }
    });
    this.allSelected = true;
  }

  deselectAll() {
    this.select.options.forEach((item: MatOption) => item.deselect());
    this.allSelected = false;
  }

}

