@Component({
  selector: 'bit-slider',
  templateUrl: './slider.component.html',
  styleUrls: ['./slider.component.scss']

})
export class SliderComponent implements AfterViewInit {
  /**
   * Is the Slider info
   */
  @Input() slideInfo: SliderInfo[];

  /**
   * Slide Move click handler
   */
  @Output() slideMove = new EventEmitter();

  /**
   * Banner click handler
   */
  @Output() slideClick = new EventEmitter();

  private currentSlide: SliderInfo;

  @ViewChild('myCarousel') myCarousel: NguCarousel<any>;

  carouselTileConfig: NguCarouselConfig = {
    grid: {xs: 1, sm: 1, md: 1, lg: 1, all: 0},
    slide: 1,
    speed: 600,
    interval: {timing: 4000},
    point: {
      visible: true
    },
    load: 2,
    touch: true,
    loop: true,
    custom: 'banner'
  };  

  constructor() {}

  ngAfterViewInit() {
    if (this.slideInfo.length !== 0) {
      this.currentSlide = this.slideInfo[0];
    }
    console.log(this.currentSlide);
  }

  onSlideMove(data: NguCarouselStore) {
    this.currentSlide = this.slideInfo[data.currentSlide]; 
    this.slideMove.emit(this.currentSlide);
  }

  onSlideClick() {
    this.slideClick.emit(this.currentSlide);
  }
}

import { Component, OnInit, Input, EventEmitter, Output, ViewChild, AfterViewInit } from '@angular/core';
import {SelectionModel} from '@angular/cdk/collections';
import {MatTableDataSource } from '@angular/material/table';
import {MatPaginator} from '@angular/material/paginator';


export interface TableHeader {
  key: string,
  text: string,
  orden?: string
  hide?: boolean,
  class?: string
}

@Component({
  selector: 'bit-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.scss']
})
export class TableComponent implements OnInit, AfterViewInit {

  private dataSource: MatTableDataSource<any>;
  private _headers: TableHeader[];
  private _enableSelection: boolean;
  columnsToDisplay: string[];
  selection = new SelectionModel<any>(true, []);

  @ViewChild(MatPaginator) paginator: MatPaginator;

  @Input()
  set headers(headers: TableHeader[]){
    this._headers = headers;

    this.columnsToDisplay = [];
    if (this._enableSelection) {
      this.columnsToDisplay = ['select'];
    } else {
      this.selection.clear();
    }
    this.columnsToDisplay = [...this.columnsToDisplay, ...this._headers.map(header => header.key)]


  }
  get headers() {
    return this._headers;
  }

  // dataSource = new MatTableDataSource<PeriodicElement>(ELEMENT_DATA);
  @Input()
  set data(data: any) {
    this.dataSource = new MatTableDataSource<any>(data);
  }
  get data() {
    return this.dataSource;
  }

  @Input()
  set enableSelection(enableSelection: boolean) {
    this._enableSelection = enableSelection;
    this.columnsToDisplay = [];
    if (this._enableSelection) {
      this.columnsToDisplay = ['select'];
    } else {
      this.selection.clear();
    }
    this.columnsToDisplay = [...this.columnsToDisplay, ...this._headers.map(header => header.key)]

  }

  @Input()
  enablePagination: boolean

  @Output()
  rowClick: EventEmitter<any> = new EventEmitter();

  constructor() { 

  }

  ngOnInit(): void {

  }

  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
  }

  isAllSelected() {
    const numSelected = this.selection.selected.length;
    const numRows = this.data.length;
    return numSelected === numRows;
  }

  masterToggle() {
    if (this.isAllSelected()) {
      this.selection.clear();
      return;
    }

    this.selection.select(...this.data);
  }

  /** The label for the checkbox on the passed row */
  checkboxLabel(row?: any): string {
    if (!row) {
      return `${this.isAllSelected() ? 'deselect' : 'select'} all`;
    }
    return `${this.selection.isSelected(row) ? 'deselect' : 'select'} row ${row.position + 1}`;
  }  

  onClick(row: any){
    this.selection.toggle(row);
    this.rowClick.emit(row);
  }

}

